/* URLBar: Fix vertical alignment */

#urlbar[breakout="true"]:not([open="true"]) {
  --urlbar-height: 28px !important;
  --urlbar-toolbar-height: 32px !important;
  backdrop-filter: contrast(100%) blur(12px) saturate(25%) !important;
